import { useState } from "react";
import SearchResult from "../../components/topicList";

const Topics = (props) => {
  const [searchValue, setSearchValue] = useState("react");

  const handleSearchChange = key => { 
    setSearchValue(key);

  }

  const handleChange = (event) => {
    setSearchValue(event.target.value);
  };
  return (
    <div>
      <nav className="navbar navbar-dark bg-dark">
        <div className="navbar-brand ">Test task GraphQL  </div>
        <div className="form-inline">
          <input
            value={searchValue}
            onChange={handleChange}
            className="form-control" type="search" placeholder="Search" aria-label="Search" />
        </div>
      </nav>
      <h1><span className="text-info text-uppercase">{searchValue}</span></h1>
      <SearchResult key={searchValue} value={searchValue} onChange={handleSearchChange} ></SearchResult>
    </div>
  )
}
export default Topics;
