import { useQuery } from "@apollo/client";
import gql from "graphql-tag";
import { useState } from "react";
import SearchResult from "../../components/topicList";

const Topics = (props) => {
  const [searchValue, setSearchValue] = useState("react");

  const handleSearchChange = key => {
    //update the search term when user type in the search bar
    setSearchValue(key);

  }

  const handleChange = (e) => {
    setSearchValue(e.target.value);
  };
  return (
    <div>
      <nav className="navbar navbar-dark bg-dark">
        <div className="navbar-brand ">Test task GraphQL  </div>
        <div className="form-inline">
          <input
            value={searchValue}
            onChange={handleChange}
            className="form-control" type="search" placeholder="Search" aria-label="Search" />
          <button className="btn btn-outline-danger" type="submit"
            onClick={() => handleSearchChange(searchValue)}
          >Search</button>
        </div>
      </nav>
      <h1>Topic : <span class="text-danger">{searchValue}</span></h1>
      <h4>Stargrazers : { }</h4>
      <SearchResult key={searchValue} value={searchValue} onChange={handleChange} ></SearchResult>
    </div>
  )
}
export default Topics;
