import React, { useState } from "react";
import SearchResult from "./topicList";

const Topics = (props) => {
  const [searchTerm, setSearchTerm] = useState("react");

  const handleSearchChange = key => { 
    setSearchTerm(key);
  }

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };
  return (
    <React.Fragment>
      <nav className="navbar navbar-dark bg-dark">
        <div className="navbar ">Test task GraphQL</div>
        <div className="form">
          <input
            value={searchTerm}
            onChange={handleChange}
            className="form-control" type="search" placeholder="Search" aria-label="Search" />
              <button className="btn btn-outline-primary my-2 my-sm-0" type="submit"
                onClick={() => props.onSearchInputChange(searchTerm)}>Search</button>
        </div>
      </nav>
      <h1><span className="text-info text-uppercase">{props.searchValue}</span></h1>
      <SearchResult key={props.searchValue} value={props.searchValue} onChange={props.onSearchInputChange} ></SearchResult>
    </React.Fragment>
  )
}
export default Topics;
